import { useState } from 'react';
import { useWorkoutsContext } from '../hooks/useWorkoutsContext';

const WorkoutForm = () => {
  const { dispatch } = useWorkoutsContext();
  const [title, setTitle] = useState('');
  const [load, setLoad] = useState('');
  const [reps, setReps] = useState('');
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const workout = { title, load, reps };

    const response = await fetch('/api/workouts', {
      method: 'POST',
      body: JSON.stringify(workout),
      headers: { 'Content-Type': 'application/json' },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
      setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
      setEmptyFields([]);
      setError(null);
      setTitle('');
      setLoad('');
      setReps('');
      dispatch({ type: 'CREATE_WORKOUT', payload: json });
    }
  };

  return (
    <form
      className="bg-white p-5 rounded-lg shadow-md space-y-4"
      onSubmit={handleSubmit}
    >
      <h3 className="text-xl font-bold">Add a New Workout</h3>

      <div>
        <label className="block text-sm font-medium text-gray-700">Exercise Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className={`w-full mt-2 p-2 border ${
            emptyFields.includes('title') ? 'border-red-500' : 'border-gray-300'
          } rounded`}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Load (in kg):</label>
        <input
          type="number"
          value={load}
          onChange={(e) => setLoad(e.target.value)}
          className={`w-full mt-2 p-2 border ${
            emptyFields.includes('load') ? 'border-red-500' : 'border-gray-300'
          } rounded`}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Number of Reps:</label>
        <input
          type="number"
          value={reps}
          onChange={(e) => setReps(e.target.value)}
          className={`w-full mt-2 p-2 border ${
            emptyFields.includes('reps') ? 'border-red-500' : 'border-gray-300'
          } rounded`}
        />
      </div>

      <button className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition">
        Add Workout
      </button>

      {error && (
        <div className="mt-4 p-2 text-sm text-red-600 bg-red-100 border border-red-500 rounded">
          {error}
        </div>
      )}
    </form>
  );
};

export default WorkoutForm;
