import { useWorkoutsContext } from '../hooks/useWorkoutsContext';
import formatDistanceToNow from 'date-fns/formatDistanceToNow';

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useWorkoutsContext();

  const handleClick = async () => {
    const response = await fetch(`/api/workouts/${workout._id}`, { method: 'DELETE' });
    const json = await response.json();

    if (response.ok) {
      dispatch({ type: 'DELETE_WORKOUT', payload: json });
    }
  };

  return (
    <div className="bg-white rounded-lg p-5 shadow-md hover:shadow-lg transition">
      <h4 className="text-lg font-semibold text-blue-600">{workout.title}</h4>
      <p className="text-sm text-gray-600">
        <strong>Load (kg): </strong>
        {workout.load}
      </p>
      <p className="text-sm text-gray-600">
        <strong>Number of reps: </strong>
        {workout.reps}
      </p>
      <p className="text-xs text-gray-500">
        {formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}
      </p>
      <span
        className="material-symbols-outlined text-gray-600 cursor-pointer hover:bg-gray-200 rounded-full p-2"
        onClick={handleClick}
      >
        delete
      </span>
    </div>
  );
};

export default WorkoutDetails;
